ls -Recurse $args[0] | Where-Object { $_.Extension -eq ".h" -or $_.Extension -eq ".cpp" } | foreach {clang-format -i -style="{BasedOnStyle: Mozilla, IndentWidth: 4, PointerAlignment: Right, IndentCaseLabels: true, BinPackParameters: false, BinPackArguments: false, NamespaceIndentation: All, AlignOperands: true, AlignTrailingComments: true}" -sort-includes (Resolve-Path -Relative $_.FullName)}
